using Microsoft.EntityFrameworkCore;using VendaDeIngresso.Api.Models;namespace VendaDeIngresso.Api.Data;public class CinemaContext : DbContext{    public CinemaContext(DbContextOptions<CinemaContext> options)        : base(options) { }    public DbSet<Sala> Sala { get; set; }    public DbSet<Assento> Assento { get; set; }    public DbSet<Reserva> Reserva { get; set; }    public DbSet<Venda> Venda { get; set; }}